import React, { Component } from 'react';
import AddProduct from './AddProduct';
import ProductList from './ProductList';
import NewProductForm from './NewProductForm';

class ProductControl extends Component {
   constructor(props){
       super(props);
       this.state ={
           productFormVisible: false
       }
   }
   render() {
    let currentVisibleState = null;
    let addProductButton = null //new code
    if (this.state.productFormVisible){
        currentVisibleState = <NewProductForm />
    }else{
        currentVisibleState = <ProductList />
        addProductButton = <button onClick={this.handleClick}>Add Product</button> //new code
    }
    return (
        <React.Fragment>
            {addProductButton}
            {currentVisibleState}
        </React.Fragment>
    )
}
}

export default ProductControl;
